$(window).on("load resize", function () {
  if ($(window).width() <= 767) {
    $(".slider-advantages:not(.slick-initialized)").slick({
      arrows: false,
      dots: true,
      infinite: true,
      speed: 400,
      slidesToShow: 1
    });
    sliderMobileSlick();
  } else {
    $(".slider-advantages.slick-initialized, .box.slick-initialized").slick("unslick");

    deletClassMobile();
  }
});

function sliderMobileSlick() {
  $(".box:not(.slick-initialized)").slick({
    dots: true,
    arrows: false,
    infinite: true,
    speed: 400,
    slidesToShow: 1
  });
}
$("#nav-icon1").click(function () {
  $("header nav").slideToggle();
  $("header").toggleClass("open-menu");
  $("#nav-icon1").toggleClass("open");
});

function deletClassMobile() {
  $("header nav").hide();
  $("header").removeClass("open-menu");
  $("#nav-icon1").removeClass("open");
}

$(".first-slider").slick({
  slide: ".first-slider__item",
  dots: false,
  infinite: true,
  speed: 300,
  prevArrow: ".left",
  nextArrow: ".right",
  variableWidth: true
});

// current

$(".btn--consult").magnificPopup({
  fixedContentPos: false,
  callbacks: {
    beforeOpen: function beforeOpen() {
      $("body").css({
        "overflow-y": "hidden"
      });
    },
    close: function close() {
      $("body").css({
        "overflow-y": "scroll"
      });
    }
  }
});
$(".navigation").on("click", "a", function (t) {
  var e = $(this).attr("data-scroll"),
    o = $(e).offset().top;
  console.log(o);
  $("header nav").slideToggle();
  $("header").toggleClass("open-menu");
  $("#nav-icon1").toggleClass("open");
  $("body,html").animate({
      scrollTop: o + 50
    },
    500
  );
});
var $page = $("html, body");
$('a[class*="btn--scroll"]').click(function (e) {
  e.preventDefault();
  $page.animate({
      scrollTop: $($.attr(this, "href")).offset().top
    },
    1500
  );
  return false;
});